    def validate_json_string(self, json_string):
        try:
            json.dumps(json_string)
        except Exception as err:
            print("[Result]: ", json_string)
            return False
        return True

    def check_valid_json(self, json_output):
        validated = self.validate_json_string(json_output)
        print("Test if valid", validated)
        self.assertTrue(validated, "The given response is not valid json.")

    def check_data_status(self, json_output):
        if 'status' in json_output:
            return True, json_output['status']
        else:
            print("JSON output:", json_output)
            return False, None

    def validate_data_status(self, json_output):
        status, value = self.check_data_status(json_output)
        self.assertTrue(status)
        self.assertEqual(value, 200)

    def _test_json_entities_location(self, json_response, text):
        if 'data' in json_response:
            if self.params['content'] == 0:
                self.assertTrue(('content' not in json_response['data']), "Content list in data, although it shouldn't be.")
            else:
                self.assertTrue(('content' in json_response['data']),
                                "Content list not in data, although it should be.")
            if 'entities' in json_response['data']:
                entities = json_response['data']['entities']
                # test entities
                print("Entities:",str(len(entities)))
                print(entities)
                for entity in entities:
                    print(
                        entity['id'],
                        entity['group'],
                        entity['surfaceForm'], "(",
                        entity['baseForm'],
                        entity['category'],
                        entity['method'],
                        entity['isPlural'],
                        entity['locationStart'], "-",
                        entity['locationEnd'], ")")
                    start = entity['locationStart']
                    end = entity['locationEnd']
                    self.assertEqual(text[start:end], entity['surfaceForm'])
            else:
                self.assertTrue(('entities' in json_response['data']), "Entities list not in data.")
        else:
            self.assertTrue(('data' in json_response), "Result data list not in data.")


