    def _test_xml(self):
        text="<p>Ulkomaisen tavaramerkin x haltija A oli purkanut ulkomaalaisen alihankkijansa B:n kanssa tekemänsä hankintasopimuksen ja ilmoittanut</p>"

        self.app.environ_base['Content-Type'] = 'text/xml'

        start = time.time()
        response = self.app.post('/tagger', data=text, content_type='text/xml', query_string=self.params)
        end = time.time()

        print("[STATUS] Executed XML input test in %s", str(datetime.timedelta(seconds=(end - start))))

        self.assertEqual(response.status_code, 200)
        json_response = json.loads(response.get_data(as_text=True))
        entities = json_response['data']['entities']

        # text properties
        print("Text length:", len(text))
        text_len = 0
        for t in text.splitlines():
            print(t, len(t), text_len, "-", text_len + len(t))
            text_len += len(t)

        # test entities
        for entity in entities:
            print(
                entity['id'],
                entity['group'],
                entity['surfaceForm'], "(",
                entity['baseForm'],
                entity['category'],
                entity['method'],
                entity['isPlural'],
                entity['locationStart'], "-",
                entity['locationEnd'], ")")
            start = entity['locationStart']
            end = entity['locationEnd']
            self.assertEqual(text[start:end], entity['surfaceForm'])

        self.check_valid_json(json_response)

